Histograms

Codepenguins |&gt;
  drop_na(
    flipper_length_mm,
    species
  ) |&gt;
  ggplot(
    aes(
      x = flipper_length_mm,
      fill = species
    )
  ) +
  geom_histogram(
    bins = 30,
    alpha = 0.5,
    position = "identity"
  ) +
  scale_fill_brand_d() +
  labs(
    x = "Flipper Length (mm)",
    y = "Frequency",
    fill = "Species"
  )

Density

Codepenguins |&gt;
  drop_na(
    flipper_length_mm,
    species
  ) |&gt;
  ggplot(
    aes(
      x = flipper_length_mm,
      fill = species
    )
  ) +
  geom_density(
    alpha = 0.5,
    position = "identity"
  ) +
  scale_fill_brand_d() +
  labs(
    x = "Flipper Length (mm)",
    y = "Density",
    fill = "Species"
  ) +
  theme(
    legend.position = "none"
  )
